import React, { useState } from 'react';
import DeleteIcon from '@mui/icons-material/Delete';
import DriveFileRenameOutlineIcon from '@mui/icons-material/DriveFileRenameOutline';
import EditNote from './EditNote';

function Note(props){
    const [editState, setEditState] = useState(false);

    function handleDelete(){
        props.onDelete(props.id);
    }
    function handleEditNote(){
        setEditState(true);
    }
    function cancelEditNote(){
        setEditState(false);
    }
    function saveNote(note) {
        // Send the updated note to the server
        fetch(`http://localhost:8080/noteboard/update/${props.id}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(note),
        })
        .then(response => {
            if (response.ok) {
              // Note successfully updated on the server
              console.log('Note updated successfully');
            } else {
              // Handle the error if note update fails
              console.error('Failed to update note');
            }
        })
        .catch(error => {
            console.error('An error occurred while updating the note', error);
        });
    }            
        
    return <div className='note'>
        <h1>{props.title}</h1>
        <p>{props.content}</p>
        <button onClick={handleDelete}><DeleteIcon style={{ color: '#0b8ee5' }}/></button>
        <button onClick={handleEditNote}><DriveFileRenameOutlineIcon style={{ color: '#0b8ee5'}}/></button>
        {editState && <EditNote 
            onCancel={cancelEditNote}
            onSave={saveNote} />}
    </div>
}
export default Note;
