import React, {useEffect, useState} from 'react';
import Header from './header';
import Footer from './footer';
import Note from './note';
import CreateArea from './createArea';


function App(){
    const [notes, setNotes] = useState([])

    useEffect(()=>{
        fetch("http://localhost:8080/noteboard/all")
        .then(res=>res.json())
        .then((result)=>{
            setNotes(result)
        })
    },[])

     
    function addNote(note){
        /*
        setNotes(prevNotes =>{
            return [...prevNotes, note];
        })  */

        /*Below code for sending data(adding note) to Backend*/
        if(notes.length < 3 ){
            fetch("http://localhost:8080/noteboard/add",{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(note)
            }).then((addedNoteId)=>{
                const addedNote = { id: addedNoteId, ...note };
                setNotes(prevNotes =>[...prevNotes, addedNote]);    /**show added note instantly */
                console.log("Note Added!")
            }).catch(error => {
                console.log("Error in adding the note");
            });
        }
        else{
            alert("Maximum limit reached!\nDelete some notes and then try to add.")
        }
    } 

    function deleteNote(key){
        /*
        setNotes(prevNotes =>{
            return prevNotes.filter((noteItem, index)=>{
                return index !== id;
            })
        })  */

        /*Below code for deleting data in Backend*/
        fetch(`http://localhost:8080/noteboard/remove/${key}`, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json',
            },
        })
        .then(response => response.json())
        .then(() => {
            const updatedNotes = notes.filter(note => note.id !== key); /**because the response is not an array but an object */
            setNotes(updatedNotes);
            console.log("Note Deleted!");
        })
        .catch(error => {
            console.log("Error in deleting note:", error);
        });

    }
    return <div>
        <Header />
        <CreateArea onAdd={addNote}/>
        {notes.map((noteItem) => {
            return <Note
            /** Frontend code below
            key={index}
            id={index} */
            key={noteItem.id}
            title={noteItem.title}
            content={noteItem.content} 
            onDelete={() => deleteNote(noteItem.id)}/>
        })}
        <Footer />
        
    </div>
}
export default App;
